mapper.insert:
INSERT INTO TB_TRNR (
	  TRNR_ID
    , TRNR_PW
    , TRNR_NAME
    , TRNR_GENDER
    , TRNR_PHOTO
    , TRNR_INTRO
    , TRNR_AMT
    , CONSULT_YN
    , REFUSAL_YN
    , TRNR_LIKE
)
VALUES
(
	 #{trnrId}
	,#{trnrPw}
	,#{trnrName}
	,#{trnrGender}
	,#{trnrPhoto}
	,#{trnrIntro}
	,#{trnrAmt}
	,#{consultYn}
	,#{refusalYn}
	,#{trnrLike}
)

mapper.select:
	SELECT
		*
	FROM
		TB_TRNR
    WHERE TRNR_ID = #{trnrId}
      AND TRNR_PW = #{trnrPw}
