mapper.insert:
INSERT INTO TB_TRNR (
	  TRNR_ID
    , TRNR_PW
    , TRNR_NAME
    , TRNR_GENDER
    , TRNR_PHOTO
    , TRNR_INTRO
    , TRNR_AMT
    , CONSULT_YN
    , REFUSAL_YN
    , TRNR_LIKE
)
VALUES
(
	 #{trnrId}
	,#{trnrPw}
	,#{trnrName}
	,#{trnrGender}
	,#{trnrPhoto}
	,#{trnrIntro}
	,#{trnrAmt}
	,#{consultYn}
	,#{refusalYn}
	,#{trnrLike}
)

mapper.select:
	SELECT
		*
	FROM
		TB_TRNR
    WHERE TRNR_ID = #{trnrId}

      AND TRNR_PW = #{trnrPw}

mapper.update:
UPDATE TB_TRNR
   SET TRNR_NAME  = #{trnrName}      
    , TRNR_GENDER = #{trnrGender}    
    , TRNR_PHOTO  = #{trnrPhoto}     
    , TRNR_INTRO  = #{trnrIntro}     
    , TRNR_AMT    = #{trnrAmt}       
    , CONSULT_YN  = #{consultYn}     
    , REFUSAL_YN  = #{refusalYn}     
    , TRNR_LIKE   = #{trnrLike}
 WHERE TRNR_ID = #{trnrId}
